const isDesktop=navigator.userAgent.indexOf("Windows")>0||navigator.userAgent.indexOf("Mac")>0,isMobile=navigator.connection.type==="wifi"&&navigator.connection.effectiveType==="4g",loadWebFonts=()=>{const a=document.getElementById("google-fonts");a.href=a.dataset.href};window.addEventListener("load",()=>{(isDesktop||isMobile)&&loadWebFonts()})